const postsReducer = (posts=[],action)=>{

    switch(action.type){
        case 'FETCH_ALL':
            return action.payload;

        case 'CREATE':
            return {
                posts,
               posts: [...posts,action.payload]};

         default :
            return posts;   

    }

};

export default postsReducer;
